query:
query video($id: String!, $commentFirst: Int!, $commentOffset: Int) {
  video(id: $id) {
    ...VideoFragmentV3
    downloadableVideo {
      url
      fileSize
      resolutionHorizontal
      resolutionVertical
      __typename
    }
    reaction(sla: Factual) {
      mine
      __typename
    }
    audioType
    isInteractive
    isLiveStream
    isEncrypted
    liveStartScheduled
    author {
      ...UserFragmentV4
      __typename
    }
    categories {
      id
      name
      __typename
    }
    tags {
      name
      __typename
    }
    extraDates {
      published
      __typename
    }
    comments(first: $commentFirst, offset: $commentOffset) {
      totalCount
      nodes {
        ...CommentFragmentV1
        replies(first: 1, offset: 0) {
          totalCount
          nodes {
            ...CommentFragmentV1
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    recommendedVideos(count: 8) {
      ...VideoFragmentV3
      __typename
    }
    __typename
  }
}

fragment VideoFragmentV3 on Video {
  ...VideoFragmentV2
  defaultDate
  commentCount(sla: Factual)
  reaction(sla: Factual) {
    like
    dislike
    __typename
  }
  __typename
}

fragment VideoFragmentV2 on Video {
  ...VideoFragmentV1
  description
  duration(unit: MILLISECOND)
  isLivePreview
  isPremiumContent
  isPremiumContentPaid
  liveStartScheduled
  premiumContentPrice
  publishStatus
  stereoscopicType
  thumbnails {
    jpgThumbnail720x405
    __typename
  }
  feature {
    id
    __typename
  }
  trailer {
    id
    __typename
  }
  __typename
}

fragment VideoFragmentV1 on Video {
  type
  id
  name
  isLiveStream
  author {
    ...UserFragmentV1
    __typename
  }
  __typename
}

fragment UserFragmentV1 on User {
  type
  id
  name
  thumbnails {
    userProfileLight
    __typename
  }
  __typename
}

fragment UserFragmentV4 on User {
  ...UserFragmentV3
  videos(representation: 0) {
    totalCount
    __typename
  }
  __typename
}

fragment UserFragmentV3 on User {
  ...UserFragmentV2
  description
  thumbnails {
    profileBg1440x420
    __typename
  }
  __typename
}

fragment UserFragmentV2 on User {
  ...UserFragmentV1
  followersCount
  iAmFollowing
  __typename
}

fragment CommentFragmentV1 on Comment {
  id
  abuseReported
  author {
    ...UserFragmentV1
    __typename
  }
  createdAt
  text
  renderedText
  votesUp
  votesDown
  votedUp
  votedDown
  isRestricted
  __typename
}
